/* eslint-disable no-undef */

let /* map, */ mapBase64, selectedElement;
let left, topMap, mapMinX, mapMinY, mapSizeX, mapSizeY, goToTarget = false;
let zoomLevel = 0.55;
let scaleFactor = 2;

const zoomStep = 0.1;
const minZoom = 0.5;
const maxZoom = 3;
let wheelZoom = 0.55;
const minPan = -450;
const maxPan = 450 / 2;

let panOffsetX = 0;
let panOffsetY = 0;
let isPanning = false;
let startX = 0;
let startY = 0;

const go_to_pin_image = "data:image/png;base64,";
const goToPin = new Image();

let canvasOffsetX, canvasOffsetY;

let data;


window.onload = function() {
	(async () => {
		const socket = new WebSocket("ws://" + window.location.hostname + ":7906");

		socket.onopen = () => {
			console.log("Connected to WebSocket server");

			const commandGetRobots = {};
			commandGetRobots["command"] = "getRobots";
			socket.send(JSON.stringify(commandGetRobots));
		};

		socket.onmessage = (event) => {
			// console.log(`Received message: ${event.data}`);
			data = JSON.parse(event.data);
			const command = data.command;
			console.log(`Received message command: ${command}`);

			switch (command) {
				case "robotList":
					console.log("Robot IDs: " + data.parameters);

					selectedElement = document.getElementById("robotSelect");

					if (selectedElement.childElementCount == 0) {
						data.parameters.forEach(robot => {
							const option = document.createElement("option");
							option.value = robot[0];
							option.text = robot[1];
							selectedElement.add(option);
						});

						getMap(selectedElement.value); // get map once for the selected robot
						selectedElement.addEventListener("change", (event) => {
							getMap(event.target.value);
						});
					}
					break;

				case "map":
					console.log(data.map);

					if (selectedElement.value == data.duid) {
						mapBase64 = data.base64;
						// map = data.map;
						scaleFactor = data.scale;
						left = data.map.IMAGE.position.left;
						topMap = data.map.IMAGE.position.top;
						drawBackgroundImage();
					}
					break;
			}

		};

		socket.onclose = () => {
			console.log("Disconnected from WebSocket server");setTimeout(() => {
				window.onload();
			}, 10000);
		};

		function getMap(duid) {
			const commandGetMap = {};
			commandGetMap.command = "getMap";
			commandGetMap.duid = duid;
			socket.send(JSON.stringify(commandGetMap));
		}


		const canvas = document.getElementById("myCanvas");
		const ctx = canvas.getContext("2d");

		async function localCoordsToRobotCoords(imagePoint) {
			const image = new Image();
			image.src = mapBase64;
			const point = {};
			await new Promise(resolve => {
				image.onload = function() {
					const xLoc = imagePoint.x;
					const yLoc = image.height / scaleFactor - imagePoint.y;

					const xPos = Math.round((left + xLoc) * 50.0);
					const yPos = Math.round((topMap + yLoc) * 50.0);
					point.x = xPos;
					point.y = yPos;
					resolve(point);
				};
			});
			return point;
		}

		function getMouseX(x) {
			return (Math.round((x - canvas.getBoundingClientRect().left - panOffsetX) / wheelZoom + mapMinX ) / scaleFactor - 1); // - 1 because of a slight map offset
		}
		function getMouseY(y) {
			return (Math.round((y - canvas.getBoundingClientRect().top - panOffsetY) / wheelZoom + mapMinY ) / scaleFactor - 1); // - 1 because of a slight map offset
		}


		function getX(x) {
			return (Math.round((x - canvas.getBoundingClientRect().left - panOffsetX) / zoomLevel + mapMinX ) / scaleFactor - 1); // - 1 because of a slight map offset
		}
		function getY(y) {
			return (Math.round((y - canvas.getBoundingClientRect().top - panOffsetY) / zoomLevel + mapMinY ) / scaleFactor - 1); // - 1 because of a slight map offset
		}

		function robotXtoPixelX(image, robotCoord)
		{
			return (robotCoord - image.position.left) * scaleFactor;
		}
		function robotYtoPixelY(image, robotCoord)
		{
			return (image.dimensions.height/scaleFactor + image.position.top - robotCoord) * scaleFactor;
		}

		let rects = [];
		let zones = [];
		let isDragging = false;
		let isResizing = false;
		let selectedRect = null;
		let offsetX, offsetY;
		const resizeSquareBorderWidth = 5;

		function downEvent(e) {
			const clientX = e.pageX || e.touches[0].clientX;
			const clientY = e.pageY || e.touches[0].clientY;
			const mouseX = getMouseX(clientX);
			const mouseY = getMouseY(clientY);
			console.log("e.clientX: " + e.clientX + " e.clientY: " + e.clientY);
			console.log("clientX: " + clientX + " clientY: " + clientY);
			console.log("mousedown pos - x: " + mouseX + " y: " + mouseY);

			localCoordsToRobotCoords({x: mouseX, y: mouseY + 2})
				.then(point => {
					// console.log("mousedown robot pos: " + JSON.stringify(point));
					console.log("Robot coords: " + JSON.stringify([point.x, point.y]));
					if (goToTarget == true) {
						const data = {};
						data.duid = selectedElement.value;
						data.command = "app_goto_target";
						data.parameters = [point.x, point.y];
						socket.send(JSON.stringify(data));
						goToTarget = false;
						stop.disabled = false;
						drawRects();
					}
				})
				.catch(error => {
					console.error("Error: " + error);
				});

			for (let i = 0; i < rects.length; i++) {
				const rect = rects[i];
				console.log("rect.x: " + rect.x + " rect.y: " + rect.y + " rect.width: " + rect.width + " rect.height: " + rect.height);

				// offsetX = clientX - rect.x + 1 - panOffsetX;
				// offsetY = clientY - rect.y + 1 - panOffsetY;
				offsetX = mouseX - rect.x + 1;
				offsetY = mouseY - rect.y + 1;
				console.log("rect: " + JSON.stringify(rect));
				console.log("mousedown offset - x: " + offsetX + " y: " + offsetY);
				console.log("left offset: " + canvasOffsetX + " top offset: " + canvasOffsetY);

				if (
					// offsetX - canvasOffsetX > rect.width - resizeSquareBorderWidth &&
					// offsetX - canvasOffsetX <= rect.width + resizeSquareBorderWidth &&
					// offsetY - canvasOffsetY > rect.height - resizeSquareBorderWidth &&
					// offsetY - canvasOffsetY <= rect.height + resizeSquareBorderWidth
					mouseX > rect.x + rect.width - resizeSquareBorderWidth &&
					mouseX <= rect.x + rect.width + resizeSquareBorderWidth &&
					mouseY > rect.y + rect.height - resizeSquareBorderWidth &&
					mouseY <= rect.y + rect.height + resizeSquareBorderWidth
				) {
					console.log("Resizing");
					offsetX = mouseX - rect.x - rect.width;
					offsetY = mouseY - rect.y - rect.height;
					selectedRect = i;
					isResizing = true;
					e.preventDefault();
				}
				else if (
					// offsetX - canvasOffsetX > 0 &&
					// offsetX - canvasOffsetX <= rect.width + 2 &&
					// offsetY - canvasOffsetY > 0 &&
					// offsetY - canvasOffsetY <= rect.height + 2
					mouseX >= rect.x &&
					mouseX <= rect.x + rect.width &&
					mouseY >= rect.y &&
					mouseY <= rect.y + rect.height
				) {
					console.log("Square selected: " + i);
					selectedRect = i;
					isDragging = true;
					e.preventDefault();
					break;
				}
			}

			if (!isResizing && !isDragging) {
				isPanning = true;
				startX = e.clientX - panOffsetX;
				startY = e.clientY - panOffsetY;
			}

			// check if a dot is clicked
			data.map.OBSTACLES2.forEach((obstacle, index) => {
				const x = robotXtoPixelX(data.map.IMAGE, obstacle[0] / 50)/scaleFactor;
				const y = robotYtoPixelY(data.map.IMAGE, obstacle[1] / 50)/scaleFactor;

				// Calculate the distance between the mouse click and the green dot's center
				const distance = Math.sqrt(Math.pow(mouseX - x, 2) + Math.pow(mouseY - y, 2));
				console.log(`distance: ${distance}`);

				// Check if the click is within the green dot (using the dot's radius)
				if (distance <= 5) {
					// The green dot was clicked, perform your action here
					console.log(`Osbstacle ${index} was clicked. The ID is ${obstacle[6]}`);

					const data = {};
					data.duid = selectedElement.value;
					data.command = "get_photo";
					data.attribute = obstacle[6];
					socket.send(JSON.stringify(data));
				}
			});
		}
		canvas.addEventListener("mousedown", downEvent);
		canvas.addEventListener("touchstart", downEvent);

		function upEvent(e) {
			isDragging = false;
			isResizing = false;
			isPanning = false;
			// drawRects();
			if (rects.length > 0)
			{
				convertToRobotZone(rects[selectedRect]);
			}
			e.preventDefault(false);
		}
		canvas.addEventListener("mouseup", upEvent);
		canvas.addEventListener("touchend", upEvent);

		canvas.addEventListener("mouseleave", () => {
			isPanning = false;
		});


		function moveEvent(e) {
			const clientX = e.pageX || e.touches[0].clientX;
			const clientY = e.pageY || e.touches[0].clientY;
			const mouseX = getMouseX(clientX);
			const mouseY = getMouseY(clientY);

			if (isDragging) {
				const draggingX = mouseX - offsetX + 1;
				const draggingY = mouseY - offsetY + 1;
				console.log("dragging mouseX pos with offset: " + draggingX);
				console.log("dragging mouseY pos with offset: " + draggingY);
				rects[selectedRect].x = draggingX;
				rects[selectedRect].y = draggingY;
				drawRects();
			}
			else if (isResizing) {
				rects[selectedRect].width = mouseX - rects[selectedRect].x - offsetX;
				rects[selectedRect].height = mouseY - rects[selectedRect].y - offsetY;
				drawRects();
			}
			else if (goToTarget)
			{
				const goToX = clientX + 5;
				const goToY = clientY + 5;

				drawRects();
				ctx.drawImage(goToPin, goToX - goToPin.width/2, goToY - goToPin.height/2 - 12, goToPin.width/2, goToPin.height/2);
			}
			else if (isPanning) {
				panOffsetX = e.clientX - startX;
				panOffsetY = e.clientY - startY;

				panOffsetX = Math.max(Math.min(panOffsetX, maxPan), minPan*wheelZoom/zoomLevel + maxPan);
				panOffsetY = Math.max(Math.min(panOffsetY, maxPan), minPan*wheelZoom/zoomLevel + maxPan);

				updateBackgroundPosition();
				drawRects();
			}
		}
		canvas.addEventListener("mousemove", moveEvent);
		canvas.addEventListener("touchmove", moveEvent);

		canvas.addEventListener("wheel", (e) => {
			e.preventDefault();

			const clientX = e.pageX || e.touches[0].clientX;
			const clientY = e.pageY || e.touches[0].clientY;
			const prevZoom = wheelZoom;

			if (e.deltaY < 0) {
				wheelZoom += zoomStep;
			} else {
				wheelZoom -= zoomStep;
			}
			wheelZoom = Math.min(Math.max(wheelZoom, minZoom), maxZoom);

			panOffsetX -= Math.round((clientX - panOffsetX) * (1 - prevZoom / wheelZoom));
			panOffsetY -= Math.round((clientY - panOffsetY) * (1 - prevZoom / wheelZoom));

			updateBackgroundPosition();
			drawRects();

			if (rects.length > 0)
			{
				convertToRobotZone(rects[selectedRect]);
			}
		});

		const deleteButton = document.getElementById("deleteButton");
		deleteButton.addEventListener("click", function() {
			if (rects[selectedRect]) {
				rects.splice(selectedRect, 1);
				console.log("rects.length: " + rects.length);
				if (rects.length < 5) {
					addButton.disabled = false;
				}
				if (rects.length < 1) {
					deleteButton.disabled = true;
					start.disabled = true;
				}
				selectedRect = rects.length - 1;
				drawRects();
			}
		});

		const addButton = document.getElementById("addButton");
		addButton.addEventListener("click", function() {
			goToTarget = false;
			const x = 186;
			const y = 184;
			// const x = (Math.round((186 * scaleFactor - mapMinX) * zoomLevel) + 2);
			// const y = (Math.round((184 * scaleFactor - mapMinY) * zoomLevel) + 3);
			const width = 25;
			const height = 25;
			// const width = Math.round(25 * scaleFactor * zoomLevel);
			// const height = Math.round(25 * scaleFactor * zoomLevel);

			rects.push({ x: x, y: y, width: width, height: height });
			console.log("length: " + rects.length);
			if (rects.length > 0) {
				deleteButton.disabled = false;
				start.disabled = false;
			}
			if (rects.length > 4) {
				addButton.disabled = true;
			}
			console.log("Square spawned at: " + x + ":" + y);
			selectedRect = rects.length - 1;
			drawRects();
			convertToRobotZone({ x: x, y: y, width: width, height: height });
		});

		const start = document.getElementById("startButton");
		start.addEventListener("click", function() {
			const data = {};
			data.duid = selectedElement.value;
			if (zones.length > 0) {
				data.command = "app_zoned_clean";
			}
			else {
				data.command = "app_start";
			}
			data.parameters = zones;
			console.log("Zones to start with: " + JSON.stringify(zones));
			start.disabled = true;
			addButton.disabled = true;
			deleteButton.disabled = true;
			socket.send(JSON.stringify(data));
			rects = [];
			drawRects();

			startButton.style.display = "none";
			pauseButton.style.display = "inline-block";
		});

		const pause = document.getElementById("pauseButton");
		pause.addEventListener("click", function() {
			const data = {};
			data.duid = selectedElement.value;
			data.command = "app_pause";
			start.disabled = false;
			socket.send(JSON.stringify(data));

			startButton.style.display = "inline-block";
			pauseButton.style.display = "none";
		});

		const stop = document.getElementById("stopButton");
		stop.addEventListener("click", function() {
			const data = {};
			data.duid = selectedElement.value;
			data.command = "app_stop";
			start.disabled = false;
			addButton.disabled = false;
			socket.send(JSON.stringify(data));

			startButton.style.display = "inline-block";
			pauseButton.style.display = "none";
		});

		const dock = document.getElementById("dockButton");
		dock.addEventListener("click", function() {
			const data = {};
			data.duid = selectedElement.value;
			data.command = "app_charge";
			socket.send(JSON.stringify(data));
		});

		const goTo = document.getElementById("goToButton");
		goTo.addEventListener("click", function() {
			goToTarget = true;
			goToPin.src = go_to_pin_image;
		});

		const resetZoom = document.getElementById("resetZoomButton");
		resetZoom.addEventListener("click", function() {
			panOffsetX = 0;
			panOffsetY = 0;
			wheelZoom = zoomLevel;
			updateBackgroundPosition();
			drawRects();
		});

		function drawBackgroundImage() {
			const image = new Image();
			// console.log("mapBase64:" + mapBase64);
			image.src = mapBase64;
			image.onload = function() {
				canvasOffsetX = canvas.getBoundingClientRect().left;
				canvasOffsetY = canvas.getBoundingClientRect().top;
				console.log("canvasOffsetX: " + canvasOffsetX);
				console.log("canvasOffsetY: " + canvasOffsetY);

				// Get the image data and calculate the actual dimensions of the image
				const tempCanvas = document.createElement("canvas");
				tempCanvas.width = image.width;
				tempCanvas.height = image.height;
				const tempCtx = tempCanvas.getContext("2d");
				tempCtx.drawImage(image, 0, 0);
				const imageData = tempCtx.getImageData(0, 0, image.width, image.height);
				mapMinX = image.width;
				mapMinY = image.height;
				let mapMaxX = 0, mapMaxY = 0;
				for (let y = 0; y < image.height; y++) {
					for (let x = 0; x < image.width; x++) {
						const index = (y * image.width + x) * 4;
						if (imageData.data[index+3] > 0) { // Check if the alpha value is non-zero
							mapMinX = Math.min(mapMinX, x);
							mapMinY = Math.min(mapMinY, y);
							mapMaxX = Math.max(mapMaxX, x);
							mapMaxY = Math.max(mapMaxY, y);
						}
					}
				}
				mapSizeX = mapMaxX - mapMinX;
				mapSizeY = mapMaxY - mapMinY;
				console.log("Image data map image.width: " + image.width + " image.height: " + image.height);
				console.log("Image data map mapSizeX: " + mapSizeX + " mapSizeY: " + mapSizeY);
				console.log("Image data mapMinX: " + mapMinX + " mapMinY: " + mapMinY + " mapMaxX: " + mapMaxX + " mapMaxY: " + mapMaxY);

				const aspectRatio = canvas.width / canvas.height;

				const contentAspectRatio = mapSizeX / mapSizeY;

				if (contentAspectRatio > aspectRatio) {
					zoomLevel = canvas.width / mapSizeX;
					wheelZoom = canvas.width / mapSizeX;
				} else {
					zoomLevel = canvas.height / mapSizeY;
					wheelZoom = canvas.height / mapSizeY;
				}
				console.log("image.width: " + image.width);
				console.log("image.height: " + image.height);
				console.log("zoomLevel: " + zoomLevel);

				// Create a temporary canvas to draw the truncated image
				const truncatedCanvas = document.createElement("canvas");
				truncatedCanvas.width = mapSizeX;
				truncatedCanvas.height = mapSizeY;
				const truncatedCtx = truncatedCanvas.getContext("2d");
				truncatedCtx.drawImage(image, mapMinX, mapMinY, mapSizeX, mapSizeY, 0, 0, mapSizeX, mapSizeY);

				const backgroundWidth = Math.round(mapSizeX * zoomLevel);
				const backgroundHeight = Math.round(mapSizeY * zoomLevel);
				canvas.style.backgroundImage = `url(${truncatedCanvas.toDataURL()})`;
				canvas.style.backgroundSize = `${backgroundWidth}px ${backgroundHeight}px`;
				canvas.style.backgroundPosition = "0 0";
				canvas.style.backgroundRepeat = "no-repeat";
			};
		}

		function updateBackgroundPosition() {
			canvas.style.backgroundPosition = `${panOffsetX}px ${panOffsetY}px`;
			const backgroundWidth = Math.round(mapSizeX * wheelZoom);
			const backgroundHeight = Math.round(mapSizeY * wheelZoom);
			canvas.style.backgroundSize = `${backgroundWidth}px ${backgroundHeight}px`;
		}

		function convertToRobotZone(rect)
		{
			const zone = [];
			console.log("rect.x : " + rect.x + " rect.y: " + rect.y);
			console.log("rect.x : " + rect.x + " rect.y: " + rect.y);

			console.log("canvasOffsetX : " + canvasOffsetX + " canvasOffsetY: " + canvasOffsetY);
			console.log("panOffsetX : " + panOffsetX + " panOffsetY: " + panOffsetY);

			console.log("x : " + getX(rect.x + canvasOffsetX + panOffsetX + 2) + " y: " + getY(rect.y + canvasOffsetY + panOffsetY + 2));
			console.log("rect.x : " + rect.x + " rect.y: " + rect.y);

			localCoordsToRobotCoords({x: getX(rect.x + canvasOffsetX + panOffsetX + 2), y: getY(rect.y + canvasOffsetY + panOffsetY + 2)}).then (coords1 => {
				console.log("coords1.x : " + coords1.x + " coords1.y: " + coords1.y);
				localCoordsToRobotCoords({x: getX(rect.x + rect.width + canvasOffsetX + panOffsetX + 2), y: getY(rect.y + rect.height + canvasOffsetY + panOffsetY+ 2)}).then (coords2 => {
					console.log("coords2.x : " + coords2.x + " coords2.y: " + coords2.y);

					zone.push(Math.min(coords1.x, coords2.x));
					zone.push(Math.min(coords1.y, coords2.y));
					zone.push(Math.max(coords1.x, coords2.x));
					zone.push(Math.max(coords1.y, coords2.y));
					zone.push(parseInt(document.getElementById("cleanCount").value)); // clean count
					zones.push(zone);
					console.log("zone: " + JSON.stringify(zone));
				});
			});
		}

		function drawRects() {
			ctx.clearRect(0, 0, canvas.width, canvas.height);
			ctx.save();
			ctx.translate(panOffsetX, panOffsetY);
			// ctx.scale(zoomLevel, zoomLevel);

			zones = [];

			for (let i = 0; i < rects.length; i++) {
				const rect = rects[i];
				const rectMinX = (Math.round((rect.x * scaleFactor - mapMinX) * wheelZoom) + 2);
				const rectMinY = (Math.round((rect.y * scaleFactor - mapMinY) * wheelZoom) + 3);
				// const rectMinX = rect.x - 1;
				// const rectMinY = rect.y - 1;
				const rectWidth = Math.round(rect.width * scaleFactor * wheelZoom);
				const rectHeight = Math.round(rect.height * scaleFactor * wheelZoom);
				// const rectWidth = rect.width;
				// const rectHeight = rect.height;

				ctx.fillStyle = "rgba(255, 255, 255, 0.5)";
				ctx.fillRect(rectMinX, rectMinY, rectWidth, rectHeight);
				ctx.lineWidth = wheelZoom * scaleFactor;
				ctx.strokeStyle = "rgba(255, 255, 255, 1)";
				ctx.strokeRect(rectMinX, rectMinY, rectWidth, rectHeight);

				ctx.fillStyle = "red";
				ctx.beginPath();
				ctx.arc(rectMinX + rectWidth, rectMinY + rectHeight, 5 * wheelZoom, 0, 2 * Math.PI);
				ctx.fill();

				console.log("Square position: " + JSON.stringify(rect));

				// convertToRobotZone(rect);
			}

			// ctx.beginPath();
			// ctx.arc(100, 100, 50, 0, 2 * Math.PI);
			// ctx.fillStyle = "red";
			// ctx.fill();
			// ctx.stroke();

			ctx.restore();
		}
	})();
};